using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace ConsoleApp14
{
    class Program
    {



        static bool almostPalindrome(string s)
        {

            Dictionary<char, int> diccio = new Dictionary<char, int>();

            foreach (char ch in s)
            {
                if (diccio.ContainsKey(ch))
                {
                    diccio[ch]++;
                }
                else
                {
                    diccio[ch] = 1;
                }
            }

            int impares = 0;
            foreach (KeyValuePair<char, int> kvp in diccio)
            {
                if (kvp.Value % 2 != 0)
                {
                    impares++;
                }
            }

            if (impares > 1) return false;

            int diffCount = 0;
            int left = 0;
            int right = s.Length - 1;

            while (right > left)
            {
                if (s[right--] != s[left++])
                {
                    diffCount++;
                }
            }
            return diffCount <=4;
        }

        
        static void Main(string[] args)
        {

            string sampleString = "";

            Console.WriteLine(almostPalindrome(sampleString));

            Console.ReadLine();
        }
    }
}

